import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { Terminal as TerminalIcon, X, Minimize2, Maximize2 } from 'lucide-react';

const Terminal = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [isMinimized, setIsMinimized] = useState(false);
  const [isMaximized, setIsMaximized] = useState(false);
  const [command, setCommand] = useState('');
  const [history, setHistory] = useState([
    { type: 'output', content: 'Welcome to Gabriel Smith\'s Portfolio Terminal' },
    { type: 'output', content: 'Type "help" to see available commands.' },
    { type: 'prompt', content: '' }
  ]);
  const [historyIndex, setHistoryIndex] = useState(-1);
  const inputRef = useRef(null);
  const terminalRef = useRef(null);
  const navigate = useNavigate();

  const commands = {
    help: {
      description: 'Show available commands',
      action: () => [
        { type: 'output', content: 'Available commands:' },
        { type: 'output', content: '  help     - Show this help message' },
        { type: 'output', content: '  clear    - Clear terminal screen' },
        { type: 'output', content: '  home     - Navigate to home page' },
        { type: 'output', content: '  timeline - Navigate to timeline page' },
        { type: 'output', content: '  projects - Navigate to projects page' },
        { type: 'output', content: '  resume   - Navigate to resume page' },
        { type: 'output', content: '  contact  - Navigate to contact page' },
        { type: 'output', content: '  about    - Show information about Gabriel' },
        { type: 'output', content: '  skills   - Show technical skills' },
        { type: 'output', content: '  exit     - Close terminal' }
      ]
    },
    clear: {
      description: 'Clear terminal screen',
      action: () => [{ type: 'clear' }]
    },
    home: {
      description: 'Navigate to home page',
      action: () => {
        navigate('/');
        return [{ type: 'output', content: 'Navigating to home page...' }];
      }
    },
    timeline: {
      description: 'Navigate to timeline page',
      action: () => {
        navigate('/timeline');
        return [{ type: 'output', content: 'Navigating to timeline page...' }];
      }
    },
    projects: {
      description: 'Navigate to projects page',
      action: () => {
        navigate('/projects');
        return [{ type: 'output', content: 'Navigating to projects page...' }];
      }
    },
    resume: {
      description: 'Navigate to resume page',
      action: () => {
        navigate('/resume');
        return [{ type: 'output', content: 'Navigating to resume page...' }];
      }
    },
    contact: {
      description: 'Navigate to contact page',
      action: () => {
        navigate('/contact');
        return [{ type: 'output', content: 'Navigating to contact page...' }];
      }
    },
    about: {
      description: 'Show information about Gabriel',
      action: () => [
        { type: 'output', content: 'Gabriel Smith - Junior Developer' },
        { type: 'output', content: 'Computer Science student at University of Toronto' },
        { type: 'output', content: 'Passionate about AI, machine learning, and software development' },
        { type: 'output', content: 'Experience with React, Python, Java, and various frameworks' },
        { type: 'output', content: 'Creator of innovative projects like Stroku and Battleship Solitaire AI' }
      ]
    },
    skills: {
      description: 'Show technical skills',
      action: () => [
        { type: 'output', content: 'Programming Languages:' },
        { type: 'output', content: '  • Python, Java, JavaScript, TypeScript, C++, C' },
        { type: 'output', content: 'Frameworks & Libraries:' },
        { type: 'output', content: '  • React, Node.js, Express, Django, Flask' },
        { type: 'output', content: 'Tools & Technologies:' },
        { type: 'output', content: '  • Git, Docker, AWS, MongoDB, PostgreSQL, Redis' },
        { type: 'output', content: 'AI/ML:' },
        { type: 'output', content: '  • TensorFlow, PyTorch, Scikit-learn, OpenCV' }
      ]
    },
    exit: {
      description: 'Close terminal',
      action: () => {
        setIsOpen(false);
        return [{ type: 'output', content: 'Terminal closed.' }];
      }
    }
  };

  const executeCommand = (cmd) => {
    const trimmedCmd = cmd.trim().toLowerCase();
    
    // Add command to history
    const newHistory = [...history, { type: 'input', content: `gabriel@portfolio:~$ ${cmd}` }];
    
    if (commands[trimmedCmd]) {
      const result = commands[trimmedCmd].action();
      if (result[0]?.type === 'clear') {
        setHistory([{ type: 'output', content: 'Terminal cleared.' }, { type: 'prompt', content: '' }]);
      } else {
        setHistory([...newHistory, ...result, { type: 'prompt', content: '' }]);
      }
    } else {
      setHistory([...newHistory, { type: 'output', content: `Command not found: ${cmd}. Type "help" for available commands.` }, { type: 'prompt', content: '' }]);
    }
    
    setCommand('');
    setHistoryIndex(-1);
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      if (command.trim()) {
        executeCommand(command);
      } else {
        setHistory([...history, { type: 'prompt', content: '' }]);
      }
    } else if (e.key === 'ArrowUp') {
      e.preventDefault();
      if (historyIndex < history.length - 1) {
        const newIndex = historyIndex + 1;
        setHistoryIndex(newIndex);
        const prevCommand = history[history.length - 1 - newIndex]?.content?.replace('gabriel@portfolio:~$ ', '') || '';
        setCommand(prevCommand);
      }
    } else if (e.key === 'ArrowDown') {
      e.preventDefault();
      if (historyIndex > 0) {
        const newIndex = historyIndex - 1;
        setHistoryIndex(newIndex);
        const prevCommand = history[history.length - 1 - newIndex]?.content?.replace('gabriel@portfolio:~$ ', '') || '';
        setCommand(prevCommand);
      } else {
        setHistoryIndex(-1);
        setCommand('');
      }
    }
  };

  const scrollToBottom = () => {
    if (terminalRef.current) {
      terminalRef.current.scrollTop = terminalRef.current.scrollHeight;
    }
  };

  useEffect(() => {
    scrollToBottom();
  }, [history]);

  useEffect(() => {
    if (isOpen && inputRef.current) {
      inputRef.current.focus();
    }
  }, [isOpen]);

  const toggleTerminal = () => {
    setIsOpen(!isOpen);
    setIsMinimized(false);
    setIsMaximized(false);
  };

  const toggleMinimize = () => {
    setIsMinimized(!isMinimized);
    setIsMaximized(false);
  };

  const toggleMaximize = () => {
    setIsMaximized(!isMaximized);
    setIsMinimized(false);
  };

  if (!isOpen) {
    return (
      <button
        id="terminal-toggle"
        onClick={toggleTerminal}
        className="fixed bottom-6 right-6 z-50 bg-accent-blue hover:bg-blue-600 text-white p-3 rounded-full shadow-lg transition-all duration-300 hover:scale-110"
      >
        <TerminalIcon size={24} />
      </button>
    );
  }

  return (
    <div className={`fixed z-50 transition-all duration-300 ${
      isMinimized ? 'bottom-0 right-6' : isMaximized ? 'inset-4' : 'bottom-6 right-6'
    } ${isMinimized ? 'w-64 h-12' : isMaximized ? 'w-auto h-auto' : 'w-96 h-80'}`}>
      <div className="terminal-window h-full flex flex-col">
        {/* Terminal Header */}
        <div className="terminal-header px-4 py-2 flex items-center justify-between rounded-t-lg">
          <div className="flex items-center space-x-2">
            <TerminalIcon size={16} className="text-gray-400" />
            <span className="text-sm text-gray-300">gabriel@portfolio: ~</span>
          </div>
          <div className="flex items-center space-x-2">
            <button
              onClick={toggleMinimize}
              className="w-3 h-3 rounded-full bg-yellow-500 hover:bg-yellow-400 transition-colors"
              title="Minimize"
            >
              <Minimize2 size={8} className="m-auto text-black" />
            </button>
            <button
              onClick={toggleMaximize}
              className="w-3 h-3 rounded-full bg-green-500 hover:bg-green-400 transition-colors"
              title="Maximize"
            >
              <Maximize2 size={8} className="m-auto text-black" />
            </button>
            <button
              onClick={toggleTerminal}
              className="w-3 h-3 rounded-full bg-red-500 hover:bg-red-400 transition-colors"
              title="Close"
            >
              <X size={8} className="m-auto text-black" />
            </button>
          </div>
        </div>

        {/* Terminal Content */}
        <div className="terminal-content flex-1 p-4 overflow-y-auto scrollbar-hide" ref={terminalRef}>
          {history.map((item, index) => (
            <div key={index} className="mb-1">
              {item.type === 'input' && (
                <div className="text-terminal-green font-mono text-sm">
                  {item.content}
                </div>
              )}
              {item.type === 'output' && (
                <div className="text-gray-300 font-mono text-sm">
                  {item.content}
                </div>
              )}
              {item.type === 'prompt' && (
                <div className="flex items-center">
                  <span className="text-terminal-green font-mono text-sm">gabriel@portfolio:~$ </span>
                  <input
                    ref={index === history.length - 1 ? inputRef : null}
                    type="text"
                    value={command}
                    onChange={(e) => setCommand(e.target.value)}
                    onKeyDown={handleKeyPress}
                    className="flex-1 bg-transparent text-terminal-green font-mono text-sm outline-none caret-terminal-green"
                    autoFocus={index === history.length - 1}
                  />
                  <span className="animate-terminal-blink text-terminal-green">█</span>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default Terminal;
